class Sausage{constructor(s,e){this.item1=s,this.item2=e}taste(){console.log(`${this.item1}맛과 ${this.item2}맛이 난다`)}}class FireSausage extends Sausage{constructor(s,e){super(s,e),this.item3="불"}taste(){console.log(`${this.item3}맛이 나기 시작한다`)}}const firesausage=new FireSausage("소고기","파");firesausage.taste();